  <div className="bg-blue-500">
    <div className="flex flex-row justify-between">
      <NavLink to="/">
        <div>
        <img src="https://cdn.icon-icons.com/icons2/1859/PNG/512/codinghtml_117947.png"/>
        </div>
      </NavLink>
      
      <div>
        <NavLink to="/">
          <p>Home</p>
        </NavLink>
      
        <NavLink to="/cart">
          <div>
            <FaShoppingCart/>
          </div>
        </NavLink>
        
      </div>
    </div>
  </div>);
};

